mapper.select:
SELECT 

FROM jf_cf a

WHERE a.id = #{id}

SELECT 

FROM jf_cf a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cfjf = #{cfjf.id}


		AND a.cftzd = #{cftzd}


		AND
		DATE_FORMAT(a.cfrq, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')


		AND DATE_FORMAT(#{overDate}, '%Y-%m-%d') >= DATE_FORMAT(a.cfrq, '%Y-%m-%d')



		AND a.cfdx = #{cfdx}


		AND a.cftd = #{cftd}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC



	limit ${limit},10

SELECT 

FROM jf_cf a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jf_cf SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
