mapper.select:
SELECT 

FROM jf_xjgc a

WHERE a.id = #{id}

SELECT 

FROM jf_xjgc a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xjjf = #{xjjf.id}


		AND
		DATE_FORMAT(a.xjsj, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')


		AND DATE_FORMAT(#{overDate}, '%Y-%m-%d') >= DATE_FORMAT(a.xjsj, '%Y-%m-%d')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC



	limit ${limit},10

SELECT 

FROM jf_xjgc a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jf_xjgc SET 
	del_flag = #{DEL_FLAG_DELETE}
