mapper.select:
SELECT 

FROM jf_xjgg a

WHERE a.id = #{id}

SELECT
FROM jf_xjgg a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.ggbt LIKE '%'||#{ggbt}||'%' '%'+#{ggbt}+'%' concat('%',#{ggbt},'%')
AND a.jfid = #{jfid.id}
AND a.sffb = #{sffb}
AND DATE_FORMAT(a.fbrq, '%Y-%m-%d') >= DATE_FORMAT(#{fbrq}, '%Y-%m-%d')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
limit ${limit},10

SELECT 

FROM jf_xjgg a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jf_xjgg SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM jf_xjgg a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.ggbt LIKE
		'%'||#{ggbt}||'%'
		'%'+#{ggbt}+'%'
		concat('%',#{ggbt},'%')


		AND a.jfid = #{jfid}


		AND a.sffb = #{sffb}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC


limit 5
